import React, { Component } from "react";
import AuthenticationService from '../../authentication/AuthenticationService'
import AssetDataService from '../../api/AssetDataService'

class ListAssetComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            assets: [],
            message: null,
            assetControl: null,            
        }
        this.loadAssets = this.loadAssets.bind(this);
        this.addAsset = this.addAsset.bind(this);       
    }

    componentDidMount() {
        this.loadAssets();        
    }

    loadAssets() {
        let username = AuthenticationService.getLoggedInUserName();        
        let token = AuthenticationService.getLoggedInToken();        
        AssetDataService.retrieveAllAssets(username, token)
            .then(
                response => {
                    this.setState(
                        {assets : response.data}                       
                    )
                }
            )                
    }

    addAsset(id) {
        this.props.history.push(`/assets/new`);
    }

    updateAsset(id) {
        this.props.history.push(`/assets/${id}`);
    }

    deleteAsset(id) {
        // let username = AuthenticationService.getLoggedInUserName();
        // AssetDataService.deleteTodo(username, id)
        // .then(
        //     response => {
        //         this.setState({message : `Delete of todo ${id} successful.`});
        //         this.refreshTodos();
        //     }
        // )
    }

    render() {        
        return (        
            <div className="text-center">
                <h1>Your Assets
                    <div className="text-right">
                        <button className="btn btn-success" onClick={this.addAsset}>Add New Asset</button>
                    </div>
                </h1>
                    {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <div >
                    <table className="table">
                        <thead>
                            <tr>                                
                                <th>Name</th>                                
                                <th>Initial Value</th>                                
                                <th>Current Value</th>
                                <th>Company</th>
                            </tr>
                        </thead>                        
                        <tbody>                            
                            {
                                this.state.assets.map(                                    
                                    asset =>
                                        <tr key={asset.id}>                                            
                                            <td>{asset.name}</td>
                                            <td>{asset.initial_value}</td>
                                            <td>{asset.current_value}</td>
                                            <td>{asset.company}</td>
                                            <td><button className="brn btn-warning" onClick={() => this.updateTodoClicked(asset.id)}>Update</button></td>
                                            <td><button className="brn btn-danger" onClick={() => this.deleteTodoClicked(asset.id)}>Delete</button></td>
                                        </tr>                                        
                                )
                            }                            
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListAssetComponent
